import styled from "styled-components"

export const PageContainer = styled.div`
    width: 100%;
    height: 100%;
`
export const PageContent = styled.div`
    padding: 3em 16em;
    height: calc(100vh - 11em) !important;
`
export const TinyText = styled.div`
    display: flex;
    align-items: center;
    flex-direction: column;
    font-size: 18px;
`
